import React from 'react';
import Form from '../../../../General/Form/form.js'
import Button from 'react-bootstrap/Button';
import './hotel.css'

class Hotel extends React.Component{
  // render() {
  //   return (
  //     <form className="form mx-5 mt-2 pb-4" action="#">
  //       {
  //         this.props.data.map(formitem =>
  //           <Form item={formitem}/>
  //         )
  //       }
  //     <Button className="searchdbtn" href="#" variant="dark">SEARCH</Button>
  //     </form>
  //   )
  // }
  constructor(props) {
    super(props);
    this.state = {
      where: '',
      checkin: '',
      checkout: ''
    }
    this.handleChangeWhere = this.handleChangeWhere.bind(this);
    this.handleChangeCheckIn = this.handleChangeCheckIn.bind(this);
    this.handleChangeCheckOut = this.handleChangeCheckOut.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }


  handleChangeWhere(event) {
   this.setState({from: event.target.value});
    }
    handleChangeCheckIn(event) {
   this.setState({to: event.target.value});
    }
    handleChangeCheckOut(event) {
    this.setState({depart: event.target.value});
    }

  handleSubmit =  (event) =>{
    event.preventDefault();
    let input = {
      "where": this.state.where,
      "checkin": this.state.checkin,
      "checkout": this.state.checkout
    }
    fetch('/users/create',
    {
      method: 'POST',
      body: JSON.stringify({input}),
      headers: {
        'Content-Type': 'application/json'
      }
    })
    .then(res => res.json())
    .then(json => this.setState({input:json}));

    const body = this.state.input
    this.setState({ dataResponse: body.msg});
    console.log("respond body" + body.user)
  }
  render() {
    return (
        <form className="form mx-5 mt-2 pb-4" onSubmit={this.handleSubmit}>
          <div className="row ">
            <div class="form-group mx-4">
              <label for="from">Leaving From:</label>
              <input 
                type="text" 
                name="from" 
                value={this.state.from}
                onChange={this.handleChangeFrom}
                class="form-control" 
                placeholder="" 
                id="from" />
            </div>
            <a className="my-auto"><i className="fas fa-exchange-alt"></i></a>
            <div class="form-group mx-4">
              <label for="to">Going To:</label>
              <input 
                type="text" 
                name="to" 
                value={this.state.to}
                onChange={this.handleChangeTo}
                class="form-control" 
                placeholder="" 
                id="to" />
            </div>
          </div>

          <div className="row ">
            <div class="form-group mx-4">
              <label for="depart">Departure:</label>
              <input 
                type="date" 
                name="depart" 
                value={this.state.depart}
                onChange={this.handleChangeDepart}
                class="form-control" 
                placeholder="" 
                id="depart" />
            </div>
            <a className="my-auto"><i className="fas fa-exchange-alt"></i></a>
            <div class="form-group mx-4">
              <label for="return">Return:</label>
              <input 
                type="date" 
                name="return" 
                value={this.state.return}
                onChange={this.handleChangeReturn}
                class="form-control" 
                placeholder="" 
                id="return" />
            </div>
          </div>
          <input type="submit" value="SEARCH" class="btn btn-dark" />
        </form>
      )
      }
}
export default Hotel
