import React from 'react';
import { withRouter } from 'react-router-dom';
import { Redirect } from 'react-router'

import './oneWay.css'

class OneWay extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      from: '',
      to:'',
      depart: '',
      responed:[],
      fireRedirect: false
    }
    this.handleChangeFrom = this.handleChangeFrom.bind(this);
    this.handleChangeTo = this.handleChangeTo.bind(this);
    this.handleChangeDepart = this.handleChangeDepart.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  
  handleChangeFrom(event) {
    this.setState({from: event.target.value});
     }
   handleChangeTo(event) {
    this.setState({to: event.target.value});
     }
   handleChangeDepart(event) {
     this.setState({depart: event.target.value});
     }
     componentWillRecieveProps(nextProps) {
      this.setState({ fireRedirect: false })
    }
     handleSubmit =  (event) =>{
      event.preventDefault();
      let input = {
        "from": this.state.from,
        "to": this.state.to,
        "depart": this.state.depart
      }
      fetch('/flight/oneway',
      {
        method: 'POST',
        body: JSON.stringify({input}),
        headers: {
          'Content-Type': 'application/json'
        }
      })
      .then(response => response.json())
      .then(json => this.setState({ responed: json }))
      // console.log(this.state.responed)
      this.setState({ fireRedirect: true })
    }
    render() {
      const { fireRedirect } = this.state
      return (
        <div>
          <form className="form mx-5 mt-2 pb-4" onSubmit={this.handleSubmit}>
            <div className="row ">
              <div class="form-group mx-4">
                <label for="from">Leaving From:</label>
                <input 
                  type="text" 
                  name="from" 
                  value={this.state.from}
                  onChange={this.handleChangeFrom}
                  class="form-control" 
                  placeholder="" 
                  id="from" />
              </div>
              <a className="my-auto"><i class="fas fa-arrow-right"></i></a>
              <div class="form-group mx-4">
                <label for="to">Going To:</label>
                <input 
                  type="text" 
                  name="to" 
                  value={this.state.to}
                  onChange={this.handleChangeTo}
                  class="form-control" 
                  placeholder="" 
                  id="to" />
              </div>
            </div>
  
            <div className="row ">
              <div class="form-group mx-4">
                <label for="depart">Departure:</label>
                <input 
                  type="date" 
                  name="depart" 
                  value={this.state.depart}
                  onChange={this.handleChangeDepart}
                  class="form-control" 
                  placeholder="" 
                  id="depart" />
              </div>
            </div>
            <input type="submit" value="SEARCH" class="btn btn-dark" />
          </form>
           {fireRedirect && (
            <Redirect to='/flightres'/>
          )}
          </div>
        )
      }
    }
export default withRouter(OneWay);
