import React from 'react';
// import Formitem from '../../General/formitem.js'
// import Button from 'react-bootstrap/Button';
import Input from "../../General/Form/input";
import Button from "../../General/Form/button";

class SignInForm extends React.Component{
  //   render() {
  //     return (
  //       <div className="container-fluid">
  //         <h2 className="text-center">Sign In</h2>
  //         <form className="SignInForm mx-auto">
  //           <div className="form-group">
  //               <label>Email address</label>
  //               <input required type="email" className="form-control" placeholder="Enter email" />
  //           </div>

  //           <div className="form-group">
  //               <label>Password</label>
  //               <input type="password" className="form-control" placeholder="Enter password" required/>
  //           </div>

  //           <div className="form-group">
  //               <div className="custom-control custom-checkbox">
  //                   <input type="checkbox" className="custom-control-input" id="customCheck1" />
  //                   <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
  //               </div>
  //           </div>

  //           <Button href="/personal" className="btn btn-dark btn-block">Submit</Button>
  //           <p className="forgot-password ">
  //               <a href="#">Forgot password?</a>
  //           </p>
  //           <p className="sign-up">
  //               Don't have an account ? <a href="#">sign-up</a>
  //           </p>
  //       </form>
  //     </div>
  //   )
  // }


  constructor(props) {
    super(props);

    this.state = {
      newUser: {
        username: "",
        password: ""
      },

    };
    this.handleUsername = this.handleUsername.bind(this);
    this.handlePassword = this.handlePassword.bind(this);
    this.handleFormSubmit = this.handleFormSubmit.bind(this);
  }

  /* This lifecycle hook gets executed when the component mounts */

  handleUsername(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          username: value
        }
      }),
    );
  }

  handlePassword(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          password: value
        }
      }),
    );
  }


  handleFormSubmit(e) {
    e.preventDefault();
    let userData = this.state.newUser;
    alert(userData.username+" "+ userData.password);
  }


  render() {
    return (
      <div>
        <form className="container-fluid" onSubmit={this.handleFormSubmit}>
          <Input
            inputType={"text"}
            title={"Username"}
            name={"username"}
            value={this.state.newUser.username}
            placeholder={"Enter your username"}
            handleChange={this.handleUsername}
          />{" "}
          {/* Username of the user */}

          <Input
            inputType={"text"}
            title={"Password"}
            name={"Password"}
            value={this.state.newUser.Password}
            placeholder={"Enter your Password"}
            handleChange={this.handlePassword}
          />{" "}
          {/* Username of the user */}


          <Button
            action={this.handleFormSubmit}
            type={"primary"}
            title={"Submit"}
          />{" "}
          {/*Submit */}
          
        </form>
        <p className="sign-up">
          Don't have an account ? <a href="/signup">sign-up</a>
        </p>
      </div>
    );
  }
}
export default SignInForm;
