import React from 'react';
import Formitem from '../../General/formitem.js'
import Button from 'react-bootstrap/Button';

class SignInForm extends React.Component{
  //   render() {
  //     return (
  //       <div className="container-fluid">
  //         <h2 className="text-center">Sign In</h2>
  //         <form className="SignInForm mx-auto">
  //           <div className="form-group">
  //               <label>Email address</label>
  //               <input required type="email" className="form-control" placeholder="Enter email" />
  //           </div>

  //           <div className="form-group">
  //               <label>Password</label>
  //               <input type="password" className="form-control" placeholder="Enter password" required/>
  //           </div>

  //           <div className="form-group">
  //               <div className="custom-control custom-checkbox">
  //                   <input type="checkbox" className="custom-control-input" id="customCheck1" />
  //                   <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
  //               </div>
  //           </div>

  //           <Button href="/personal" className="btn btn-dark btn-block">Submit</Button>
  //           <p className="forgot-password ">
  //               <a href="#">Forgot password?</a>
  //           </p>
  //           <p className="sign-up">
  //               Don't have an account ? <a href="#">sign-up</a>
  //           </p>
  //       </form>
  //     </div>
  //   )
  // }


  constructor(props) {
    super(props);

    this.state = {
      newUser: {
        name: "",
        age: "",
        gender: "",
        skills: [],
        about: ""
      },

      genderOptions: ["Male", "Female", "Others"],
      skillOptions: ["Programming", "Development", "Design", "Testing"]
    };
    this.handleTextArea = this.handleTextArea.bind(this);
    this.handleAge = this.handleAge.bind(this);
    this.handleFullName = this.handleFullName.bind(this);
    this.handleFormSubmit = this.handleFormSubmit.bind(this);
    this.handleClearForm = this.handleClearForm.bind(this);
    this.handleCheckBox = this.handleCheckBox.bind(this);
    this.handleInput = this.handleInput.bind(this);
  }

  /* This lifecycle hook gets executed when the component mounts */

  handleFullName(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          name: value
        }
      }),
      () => console.log(this.state.newUser)
    );
  }

  handleAge(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          age: value
        }
      }),
      () => console.log(this.state.newUser)
    );
  }

  handleInput(e) {
    let value = e.target.value;
    let name = e.target.name;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          [name]: value
        }
      }),
      () => console.log(this.state.newUser)
    );
  }

  handleTextArea(e) {
    console.log("Inside handleTextArea");
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          about: value
        }
      }),
      () => console.log(this.state.newUser)
    );
  }

  handleCheckBox(e) {
    const newSelection = e.target.value;
    let newSelectionArray;

    if (this.state.newUser.skills.indexOf(newSelection) > -1) {
      newSelectionArray = this.state.newUser.skills.filter(
        s => s !== newSelection
      );
    } else {
      newSelectionArray = [...this.state.newUser.skills, newSelection];
    }

    this.setState(prevState => ({
      newUser: { ...prevState.newUser, skills: newSelectionArray }
    }));
  }

  handleFormSubmit(e) {
    e.preventDefault();
    let userData = this.state.newUser;

    fetch("http://example.com", {
      method: "POST",
      body: JSON.stringify(userData),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      }
    }).then(response => {
      response.json().then(data => {
        console.log("Successful" + data);
      });
    });
  }

  handleClearForm(e) {
    e.preventDefault();
    this.setState({
      newUser: {
        name: "",
        age: "",
        gender: "",
        skills: [],
        about: ""
      }
    });
  }

  render() {
    return (
      <form className="container-fluid" onSubmit={this.handleFormSubmit}>
        <Input
          inputType={"text"}
          title={"Username"}
          name={"username"}
          value={this.state.newUser.username}
          placeholder={"Enter your username"}
          handleChange={this.handleInput}
        />{" "}
        {/* Username of the user */}

        <Input
          inputType={"text"}
          title={"Password"}
          name={"Password"}
          value={this.state.newUser.Password}
          placeholder={"Enter your Password"}
          handleChange={this.handleInput}
        />{" "}
        {/* Username of the user */}


        <Button
          action={this.handleFormSubmit}
          type={"primary"}
          title={"Submit"}
        />{" "}
        {/*Submit */}
        
      </form>
    );
  }
}
export default SignInForm;
