import React from 'react'

export default function TabPanel(props) {
  const { isActive, index, className, ...attributes } = props

  const getClassName = () => {
    return 'tab-pane ' + (className || '') + (isActive ? ' active ' : '')
  }

  return (
    <div
      {...attributes}
      className={getClassName()}
      role='tabpanel'
      aria-labelledby={'tab-' + index}
    >
      {props.children}
    </div>
  )
}
